mapper.select:
select hs.*, si.sclass_id, u.username, c.class_name, g.grade_name, m.major_name from examination_student hs, student_info si, class c, grade g, major m, user u where hs.examination_id=#{onlinetestID} and hs.student_id=si.sid and si.sclass_id=c.class_id and c.grade_id=g.id and g.major_id=m.major_id and si.sid=u.user_id and si.sclass_id in
(#{item})
and state=#{stateChecked} and iscomment=#{commentChecked}
and concat(u.user_id, u.username) LIKE CONCAT ('%', #{studentInfo},'%')
order by si.sclass_id, si.sid limit #{page}, #{size}

mapper.update:
update examination_student set answer=#{answer}
    ,comment = #{commentValue}
    ,iscomment = 1

where examination_id=#{homeworkID} and student_id=#{studentID}
